		$xfire_style = XFIRE_SF;
	}
	elseif($member['user_xfire_style'] == XFIRE_FANTASY)
	{
		$xfire_style = XFIRE_OS;
	}
	elseif($member['user_xfire_style'] == XFIRE_WOW)
	{
		$xfire_style = XFIRE_WO;
	}
}
